      {
        "name": "id",
        "type": "ID!",
        "description": "The draft order to complete.",
        "required": true,
        "typeName": "ID",
        "isList": false
      },
      {
        "name": "paymentPending",
        "type": "Boolean",
        "description": "Whether the payment is pending.",
        "required": false,
        "typeName": "Boolean",
        "isList": false
      },
      {
        "name": "paymentGatewayId",
        "type": "ID",
        "description": "The gateway for the completed draft order.",
        "required": false,
        "typeName": "ID",
        "isList": false
      },
      {
        "name": "sourceName",
        "type": "String",
        "description": "A channel definition handle used for sales channel attribution.",
        "required": false,
        "typeName": "String",
        "isList": false
      }
